  changeSet(author: "owf", id: "3.7.0-1", context: "create, 3.7.0", dbms: "hsqldb") {
      preConditions(onFail: "MARK_RAN", onError: "MARK_RAN") {
          not {
            tableExists(tableName:"DASHBOARD")
          }
          not {
            tableExists(tableName:"DASHBOARD_WIDGET_STATE")
          }
          not {
            tableExists(tableName:"DOMAIN_MAPPING")
          }
          not {
            tableExists(tableName:"EVENTING_CONNECTIONS")
          }
          not {
            tableExists(tableName:"OWF_GROUP")
          }
          not {
            tableExists(tableName:"OWF_GROUP_PEOPLE")
          }
          not {
            tableExists(tableName:"PERSON")
          }
          not {
            tableExists(tableName:"PERSON_WIDGET_DEFINITION")
          }
          not {
            tableExists(tableName:"PREFERENCE")
          }
          not {
            tableExists(tableName:"REQUESTMAP")
          }
          not {
            tableExists(tableName:"ROLE")
          }
          not {
            tableExists(tableName:"ROLE_PEOPLE")
          }
          not {
            tableExists(tableName:"TAG_LINKS")
          }
          not {
            tableExists(tableName:"TAGS")
          }
          not {
            tableExists(tableName:"WIDGET_DEFINITION")
          }
      }
      sql ( text = """
        CREATE MEMORY TABLE DASHBOARD(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,ALTERED_BY_ADMIN BOOLEAN NOT NULL,COLUMN_COUNT INTEGER NOT NULL,DASHBOARD_POSITION INTEGER NOT NULL,GUID VARCHAR(255) NOT NULL,ISDEFAULT BOOLEAN NOT NULL,LAYOUT VARCHAR(9) NOT NULL,NAME VARCHAR(200) NOT NULL,USER_ID BIGINT NOT NULL,CONSTRAINT SYS_CT_566 UNIQUE(GUID))
        CREATE MEMORY TABLE DASHBOARD_WIDGET_STATE(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,ACTIVE BOOLEAN NOT NULL,BUTTON_ID VARCHAR(255),BUTTON_OPENED BOOLEAN NOT NULL,COLLAPSED BOOLEAN NOT NULL,COLUMN_POS INTEGER NOT NULL,DASHBOARD_ID BIGINT NOT NULL,HEIGHT INTEGER NOT NULL,MAXIMIZED BOOLEAN NOT NULL,MINIMIZED BOOLEAN NOT NULL,NAME VARCHAR(200) NOT NULL,PERSON_WIDGET_DEFINITION_ID BIGINT,PINNED BOOLEAN NOT NULL,REGION VARCHAR(15) NOT NULL,STATE_POSITION INTEGER NOT NULL,UNIQUE_ID VARCHAR(255) NOT NULL,WIDGET_GUID VARCHAR(255),WIDTH INTEGER NOT NULL,X INTEGER NOT NULL,Y INTEGER NOT NULL,Z_INDEX INTEGER NOT NULL,CONSTRAINT SYS_CT_570 UNIQUE(UNIQUE_ID),CONSTRAINT FKB6440EA1CA944B81 FOREIGN KEY(DASHBOARD_ID) REFERENCES DASHBOARD(ID))
        CREATE MEMORY TABLE DOMAIN_MAPPING(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,DEST_ID BIGINT NOT NULL,DEST_TYPE VARCHAR(255) NOT NULL,RELATIONSHIP_TYPE VARCHAR(8),SRC_ID BIGINT NOT NULL,SRC_TYPE VARCHAR(255) NOT NULL)
        CREATE MEMORY TABLE EVENTING_CONNECTIONS(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,DASHBOARD_WIDGET_STATE_ID BIGINT NOT NULL,WIDGET_GUID VARCHAR(255) NOT NULL,EVENTING_CONNECTIONS_IDX INTEGER,CONSTRAINT FKBCC1569EB20FFC4B FOREIGN KEY(DASHBOARD_WIDGET_STATE_ID) REFERENCES DASHBOARD_WIDGET_STATE(ID))
        CREATE MEMORY TABLE OWF_GROUP(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,AUTOMATIC BOOLEAN NOT NULL,DESCRIPTION VARCHAR(255),EMAIL VARCHAR(255),NAME VARCHAR(200) NOT NULL,STATUS VARCHAR(8) NOT NULL)
        CREATE MEMORY TABLE OWF_GROUP_PEOPLE(GROUP_ID BIGINT NOT NULL,PERSON_ID BIGINT NOT NULL,PRIMARY KEY(GROUP_ID,PERSON_ID),CONSTRAINT FK28113703B197B21 FOREIGN KEY(GROUP_ID) REFERENCES OWF_GROUP(ID))
        CREATE MEMORY TABLE PERSON(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,DESCRIPTION VARCHAR(255),EMAIL VARCHAR(255),EMAIL_SHOW BOOLEAN NOT NULL,ENABLED BOOLEAN NOT NULL,LAST_LOGIN TIMESTAMP,PREV_LOGIN TIMESTAMP,USER_REAL_NAME VARCHAR(200) NOT NULL,USERNAME VARCHAR(200) NOT NULL,CONSTRAINT SYS_CT_582 UNIQUE(USERNAME))
        CREATE MEMORY TABLE PERSON_WIDGET_DEFINITION(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,PERSON_ID BIGINT NOT NULL,PWD_POSITION INTEGER NOT NULL,VISIBLE BOOLEAN NOT NULL,WIDGET_DEFINITION_ID BIGINT NOT NULL,CONSTRAINT SYS_CT_586 UNIQUE(PERSON_ID,WIDGET_DEFINITION_ID),CONSTRAINT FK6F5C17C4C1F5E0B3 FOREIGN KEY(PERSON_ID) REFERENCES PERSON(ID))
        CREATE MEMORY TABLE PREFERENCE(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,NAMESPACE VARCHAR(200) NOT NULL,PATH VARCHAR(200) NOT NULL,USER_ID BIGINT NOT NULL,VALUE LONGVARCHAR NOT NULL,CONSTRAINT SYS_CT_590 UNIQUE(PATH,NAMESPACE,USER_ID),CONSTRAINT FKA8FCBCDB8656347D FOREIGN KEY(USER_ID) REFERENCES PERSON(ID))
        CREATE MEMORY TABLE REQUESTMAP(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,CONFIG_ATTRIBUTE VARCHAR(255) NOT NULL,URL VARCHAR(255) NOT NULL,CONSTRAINT SYS_CT_594 UNIQUE(URL))
        CREATE MEMORY TABLE ROLE(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,AUTHORITY VARCHAR(255) NOT NULL,DESCRIPTION VARCHAR(255) NOT NULL,CONSTRAINT SYS_CT_598 UNIQUE(AUTHORITY))
        CREATE MEMORY TABLE ROLE_PEOPLE(ROLE_ID BIGINT NOT NULL,PERSON_ID BIGINT NOT NULL,PRIMARY KEY(ROLE_ID,PERSON_ID),CONSTRAINT FK28B75E7870B353 FOREIGN KEY(ROLE_ID) REFERENCES ROLE(ID),CONSTRAINT FK28B75E78C1F5E0B3 FOREIGN KEY(PERSON_ID) REFERENCES PERSON(ID))
        CREATE MEMORY TABLE TAG_LINKS(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,EDITABLE BOOLEAN,POS BIGINT,TAG_ID BIGINT NOT NULL,TAG_REF BIGINT NOT NULL,TYPE VARCHAR(255) NOT NULL,VISIBLE BOOLEAN)
        CREATE MEMORY TABLE TAGS(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,NAME VARCHAR(255) NOT NULL,CONSTRAINT SYS_CT_606 UNIQUE(NAME))
        CREATE MEMORY TABLE WIDGET_DEFINITION(ID BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,VERSION BIGINT NOT NULL,DISPLAY_NAME VARCHAR(200) NOT NULL,HEIGHT INTEGER NOT NULL,IMAGE_URL_LARGE VARCHAR(2083) NOT NULL,IMAGE_URL_SMALL VARCHAR(2083) NOT NULL,SINGLETON BOOLEAN NOT NULL,VISIBLE BOOLEAN NOT NULL,WIDGET_GUID VARCHAR(255) NOT NULL,WIDGET_URL VARCHAR(2083) NOT NULL,WIDGET_VERSION VARCHAR(2083) NOT NULL,WIDTH INTEGER NOT NULL,CONSTRAINT SYS_CT_610 UNIQUE(WIDGET_GUID))

        ALTER TABLE DASHBOARD ADD CONSTRAINT FKC18AEA948656347D FOREIGN KEY(USER_ID) REFERENCES PERSON(ID)
        ALTER TABLE DASHBOARD_WIDGET_STATE ADD CONSTRAINT FKB6440EA192BD68BB FOREIGN KEY(PERSON_WIDGET_DEFINITION_ID) REFERENCES PERSON_WIDGET_DEFINITION(ID)
        ALTER TABLE OWF_GROUP_PEOPLE ADD CONSTRAINT FK2811370C1F5E0B3 FOREIGN KEY(PERSON_ID) REFERENCES PERSON(ID)
        ALTER TABLE PERSON_WIDGET_DEFINITION ADD CONSTRAINT FK6F5C17C4293A835C FOREIGN KEY(WIDGET_DEFINITION_ID) REFERENCES WIDGET_DEFINITION(ID)
        ALTER TABLE TAG_LINKS ADD CONSTRAINT FK7C35D6D45A3B441D FOREIGN KEY(TAG_ID) REFERENCES TAGS(ID)
        ALTER TABLE DASHBOARD ALTER COLUMN ID RESTART WITH 13
        ALTER TABLE DASHBOARD_WIDGET_STATE ALTER COLUMN ID RESTART WITH 37
        ALTER TABLE DOMAIN_MAPPING ALTER COLUMN ID RESTART WITH 5
        ALTER TABLE EVENTING_CONNECTIONS ALTER COLUMN ID RESTART WITH 1
        ALTER TABLE OWF_GROUP ALTER COLUMN ID RESTART WITH 3
        ALTER TABLE PERSON ALTER COLUMN ID RESTART WITH 5
        ALTER TABLE PERSON_WIDGET_DEFINITION ALTER COLUMN ID RESTART WITH 65
        ALTER TABLE PREFERENCE ALTER COLUMN ID RESTART WITH 7
        ALTER TABLE REQUESTMAP ALTER COLUMN ID RESTART WITH 4
        ALTER TABLE ROLE ALTER COLUMN ID RESTART WITH 3
        ALTER TABLE TAG_LINKS ALTER COLUMN ID RESTART WITH 1
        ALTER TABLE TAGS ALTER COLUMN ID RESTART WITH 1
        ALTER TABLE WIDGET_DEFINITION ALTER COLUMN ID RESTART WITH 17

      """)
  }

